import React from "react";
import { Text, StyleSheet } from "react-native";
import theme from "./../theme";

const styles = StyleSheet.create({
  text: {
    fontSize: theme.fontSizes.body,
    color: theme.colors.textPrimary,
    fontFamily: theme.fonts.main,
    fontWeight: theme.fontWeights.normal,
  },
  colorPrimary: {
    color: theme.colors.primary,
  },
  colorSecondary: {
    color: theme.colors.textSecondary,
  },
  bold: {
    fontWeight: theme.fontWeights.bold,
  },
  subheading: {
    fontSize: theme.fontSizes.subheading,
  },
  textAlignCenter: {
    textAlign: "center",
  },
  containerFlex: {
    flexDirection: "row",
    color: 'orange'
  },
  containerJustifyContent:{
     justifyContent: "space-around"
},
});

/*Exportamos una funcion la cual renderizara los 
datos y compararÃ¡ si esta en uso o no el elemnto que se llame*/
export default function StyledText({
  children,
  color,
  textAlign,
  containerFlex,
  containerJustifyContent,
  fontSize,
  fontWeights,
  style,
  ...restOfProps
}) {
  const textStyles = [
    styles.text,
    containerFlex === "row" && styles.containerFlex,
    containerJustifyContent === "space-around" && styles.containerJustifyContent,
    textAlign === "center" && styles.textAlignCenter,
    color === "primary" && styles.colorPrimary,
    color === "secondary" && styles.colorSecondary,
    fontSize === "subheading" && styles.subheading,
    fontWeights === "bold" && styles.bold,
  ];
  return (
    <Text style={textStyles} {...restOfProps}>
      {children}
    </Text>
  );
}
