import React from "react";
import { View,StyleSheet } from "react-native";
import StyledText from "./StyledText";
import Constants from "expo-constants";
import themes from "../theme";
import { Link } from "react-router-native";

/*
Componente para el manejo de rutas
*/
const AppBarTab = ({active,children,to}) => {
  return (
    <Link to={to}>
       <StyledText fontWeights="bold" style={styles.text}>
        {children}
      </StyledText>
     
    </Link>
  )
}



/*
Componente para el manejo del header y rutas
*/
const AppBar = () => {
  return (
    <View style={styles.container}>
     <AppBarTab active to='/'>Repositorios</AppBarTab>
     <AppBarTab active to='/sign-in'>Sign-in</AppBarTab>
      

    </View>
  )
}



const styles = StyleSheet.create({
  container: {
    backgroundColor: themes.appBar.primary,
    paddingTop: Constants.statusBarHeight + 10,
    paddingBottom: 10,
    paddingLeft: 10,
  },
  text: {
    color: themes.appBar.textPrimary,
  }
})

export default AppBar
