import React from "react";
import { Image, View, StyleSheet } from "react-native";
import StyledText from "./StyledText";
import RepositoryStats from "./RepositoryStats";
import theme from "../theme";

/*
-Componente para crear estilos : StyleSheet
-Recive un arreglo de propssitorio
-No se usan {}, solo para llamadas
*/

const RepositoryItemHeader = ({
  ownerAvatarUrl,
  fullName,
  description,
  language,
}) => (
  <View style={{ flexDirection: "row", paddingBottom: 2 }}>
    <Image style={styles.avatar} source={{ uri: ownerAvatarUrl }} />
    <View style={{paddingLeft:4}}>
      <StyledText fontSize="subheading" fontWeights="bold" color="primary">
        {fullName}{" "}
      </StyledText>
      <StyledText>{description} </StyledText>
      <StyledText style={styles.language}>{language} </StyledText>
    </View>
  </View>
);

const RepositoryItem = (props) => (
  <View key={props.id} style={styles.container}>
    <RepositoryItemHeader {...props} />
    <RepositoryStats {...props} />
  </View>
);

const styles = StyleSheet.create({
  container: { padding: 20, paddingBottom: 5, paddingTop: 5 },
  language: {
    padding: 4,
    color: theme.colors.white,
    backgroundColor: theme.colors.primary,
    alignSelf: "flex-start",
    borderRadius: 4,
    overflow: "hidden",
  },
  avatar: {
    width: 48,
    height: 48,
    borderRadius: 4,
  },
});

export default RepositoryItem;
