import React from "react";
import { Text, View, StyleSheet } from "react-native";
import StyledText from "./StyledText";

/*
-Componente para crear estilos : StyleSheet
-Recive un arreglo de propssitorio
-No se usan {}, solo para llamadas
*/
const RepositoryStats = (props) => {
  return (
    <View>
      <StyledText>{props.stargazersCount} </StyledText>
      <StyledText>{props.forksCount} </StyledText>
      <StyledText>{props.reviewCount} </StyledText>
      <StyledText>{props.ratingAverage} </StyledText>
    </View>
  );
};

const RepositoryItem = (props) => (
  <View key={props.id} style={styles.container}>
    <StyledText fontSize="subheading" fontWeights="bold" color="primary">
      {props.fullName}{" "}
    </StyledText>
    <StyledText>{props.description} </StyledText>
    <StyledText>{props.language} </StyledText>
    <RepositoryStats />
  </View>
);

const styles = StyleSheet.create({
  container: { padding: 20, paddingBottom: 5, paddingTop: 5 },
});

export default RepositoryItem;
