import React from "react";
import { Text, StyleSheet } from "react-native";
import theme from "./../theme";

const styles = StyleSheet.create({
  text: {
    fontSize: theme.fontSizes.body,
    color: theme.colors.primary,
    fontFamily: theme.fonts.main,
    fontWeight: theme.fontWeights.normal,
  },
  colorPrimary: {
    color: theme.colors.primary,
  },
  bold: {
    fontWeight: theme.fontWeights.bold,
  },
  subheading: {
    fontSize: theme.fontSizes.subheading,
  },
});

/*Exportamos una funcion la cual renderizara los 
datos y comparar√° si esta en uso o no el elemnto que se llame*/
export default function StyledText({ bold, blue, big, children, small }) {
  const textStyles = [
    //Por defecto el texto es grey y de 12pz
    styles.text,
    blue && styles.blue, //Operador corto circuito
    bold && styles.bold,
    big && styles.big,
    small && styles.small,
  ];
  return <Text style={textStyles}>{children}</Text>;
}
