import React from "react";
import { Text, View, StyleSheet } from "react-native";

/*
-Componente para crear estilos : StyleSheet
-Recive un arreglo de propssitorio
-No se usan {}, solo para llamadas
*/

const RepositoryItem = (props) => (
  <View key={props.id} style={styles.container}>
    <Text style={styles.title}> Id: {props.id} </Text>
    <Text> FullName: {props.fullName} </Text>
    <Text> Description: {props.description} </Text>
    <Text> Language: {props.language} </Text>
    <Text> Starts: {props.stargazersCount} </Text>
    <Text> Forks: {props.forksCount} </Text>
    <Text> Review: {props.reviewCount} </Text>
    <Text> Rating: {props.ratingAverage} </Text>
  </View>
);

const styles = StyleSheet.create({
  container: { padding: 20, paddingBottom: 5, paddingTop: 5 },
  title : {
      color: '#09f',
      fontWeight: 'bold',
      marginBottom:5


  }
});

export default RepositoryItem;
