import React from "react";
import { Text, StyleSheet } from "react-native";
import theme from "./../theme";

const styles = StyleSheet.create({
  text: {
    fontSize: theme.fontSizes.body,
    color: theme.colors.primary,
    fontFamily: theme.fonts.main,
    fontWeight: theme.fontWeights.normal,
  },
  colorPrimary: {
    color: theme.colors.primary,
  },
  colorSecondary: {
    color: theme.colors.textSecondary,
  },
  bold: {
    fontWeight: theme.fontWeights.bold,
  },
  subheading: {
    fontSize: theme.fontSizes.subheading,
  },
});

/*Exportamos una funcion la cual renderizara los 
datos y compararÃ¡ si esta en uso o no el elemnto que se llame*/
export default function StyledText({
  children,
  colorPrimary,
  colorSecondary,
  bold,
  subheading,
  style,
  ...restOfProps
}) {
  const textStyles = [
    //Por defecto el texto es grey y de 12pz
    styles.text,
    bold && styles.bold, //Operador corto circuito

    subheading && styles.subheading,
  ];
  return <Text style={textStyles} {...restOfProps}>{children}</Text>;
}
