import React from "react";
import {View} from "react-native";
import StyledText from "./StyledText";
//Metodo para parsear los stars

const parseThousands = value => {
    return value >= 1000
    ? `${Math.round(value /100 ) / 10}k`
    :String(value)
}


const RepositoryStats = (props) => {
  return (
    <View   style={{  justifyContent: "space-around" }}>
      <View containerFlex='row'>
        <StyledText textAlign='center' fontWeights="bold">Stars</StyledText>
        <StyledText textAlign='center'>{parseThousands(props.stargazersCount)}</StyledText>
      </View>
      <View containerFlex='row'>
        <StyledText textAlign='center' fontWeights="bold">Forks</StyledText>
        <StyledText textAlign='center'>{parseThousands(props.forksCount)}</StyledText>
      </View>
      <View>
        <StyledText  textAlign='center' fontWeights="bold">Review</StyledText>
        <StyledText textAlign='center' >{props.reviewCount} </StyledText>
      </View>
      <View>
        <StyledText textAlign='center' fontWeights="bold">Rating</StyledText>
        <StyledText textAlign='center'>{props.ratingAverage} </StyledText>
      </View>
    </View>
  );
};

export default RepositoryStats
