import React from "react";
import { Text, View } from "react-native";
import repositorios from "../data/repositorios.js";

const RepositoryList = () => {
  return (
    <View>
      {/*Recorremos el arreglo con un map*/}
      {repositorios.map((repo) => (
        //le damos una llave a la vista
        <View key={repo.id} >
          {/*le damos un valor */}
          <Text> Id: {repo.id} </Text>
          <Text> FullName: {repo.fullName} </Text>
          <Text> Description: {repo.description} </Text>
          <Text> Language: {repo.language} </Text>
          <Text> Starts: {repo.stargazersCount} </Text>
          <Text> Forks: {repo.forksCount} </Text>
          <Text> Review: {repo.reviewCount} </Text>
          <Text> Rating: {repo.ratingAverage} </Text>
        </View>
      ))}
polacode
    </View>
  );
};

export default RepositoryList;
